package com.example.demo;

import org.junit.jupiter.api.Test;

import static org.assertj.core.api.AssertionsForClassTypes.assertThat;
import static org.assertj.core.api.AssertionsForClassTypes.assertThatThrownBy;

public class ImageEncoderTest {

    ImageEncoder instance = new ImageEncoder();

    @Test
    void encode_withSVGFile_mustReturnSVGDataUrlEncodeInBase64() throws Exception {
        String encodedData = instance.encode("classpath:test_files/test.svg");
        assertThat(encodedData).isEqualTo("data:image/svg+xml;base64,PD94bWwgdmVyc2lvbj0iMS4wIiBlbmNvZGluZz0iVVRGLTgiIHN0YW5kYWxvbmU9Im5vIj8+Cjxzdmc+PC9zdmc+");
    }

    @Test
    void encode_withPngFile_mustReturnPngDataUrlEncodeInBase64() throws Exception {
        String encodedData = instance.encode("classpath:test_files/test.png");
        assertThat(encodedData).isEqualTo("data:image/png;base64,");
    }

    @Test
    void encode_withNotSVGOrPngFile_throwException() {
        assertThatThrownBy(() -> instance.encode("classpath:test_files/test.any"))
                .isInstanceOf(IllegalArgumentException.class);
    }


}
